mapper.update:
UPDATE SISTBBS SET
READCOUNT=READCOUNT+1
WHERE SEQ=#{seq}

mapper.select:
SELECT SEQ, ID, REF, STEP, DEPTH, TITLE, CONTENT, WDATE, PARENT, DEL, READCOUNT FROM ( SELECT ROW_NUMBER() OVER (ORDER BY REF DESC, STEP ASC) as rnum, SEQ, ID, REF, STEP, DEPTH, TITLE, CONTENT, WDATE, PARENT, DEL, READCOUNT FROM SISTBBS WHERE 1=1
AND TITLE like '%'||#{s_keyword}||'%' AND CONTENT like '%'||#{s_keyword}||'%'
) A WHERE rnum between ${start} AND ${end}

mapper.update:
UPDATE SISTBBS SET
	TITLE=#{title}, CONTENT=#{content}, WDATE=SYSDATE
WHERE SEQ=#{seq}

UPDATE SISTBBS SET
	DEL=#{del}, WDATE=SYSDATE
WHERE SEQ=#{seq}
